import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import '../styles/StockPricePredict.css';

const StockChart = () => {
  const [stockName, setStockName] = useState("");
  const [chartData, setChartData] = useState({});

  const handleChange = (e) => {
    setStockName(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `https://api.coingecko.com/api/v3/coins/${stockName}/market_chart?vs_currency=usd&days=7&interval=daily`
      );
      const data = await response.json();
      const prices = data.prices.map((price) => price[1]);
      const dates = data.prices.map((price) => new Date(price[0]));
      setChartData({
        labels: dates,
        datasets: [
          {
            label: stockName,
            data: prices,
            fill: false,
            borderColor: "#0084FF",
          },
        ],
      });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Stock Name:
          <input type="text" value={stockName} onChange={handleChange} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <Line data={chartData} />
    </div>
  );
};

export default StockChart;
