import React, { useState } from 'react';
import './App.css'
import SideMenu from './components/SideMenu';
import Navbar from './components/Navbar';
import Table from './components/Table';
import PortfolioCard from './components/PortfolioCard';
import ChatMessage from './components/ChatMessage';
import ChatInput from './components/ChatInput';
import data1 from './data1';
import data2 from './data2';
import data3 from './data3';
import data4 from './data4';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

function App() {


  return (
    <Router>
      <Navbar/>
      <SideMenu />
      <Routes>
          <Route path="/">
            <Overview />
          </Route>
          <Route path="/Overview">
            <Overview />
          </Route>
          {/* <Route path="/user">
            <User />
          </Route>
          <Route path="/history">
          <TransacHist />
          </Route> */}
          <Route path="/chatbot">
          <ChatBot />
          </Route>

        </Routes>

    </Router>
  );
}

function Overview(){
  return(
    <>
    <div className="container">
    <PortfolioCard/>
    </div>

    <div className="container2">
      <div className="table-grid">
        <div className="table-column">
          <Table headers={['Share', 'Price', 'Change','Percent']} data={data1} heading="MY PORTFOLIO"/>
        </div>
        <div className="table-column">
          <Table headers={['Share', 'Close', 'Volume']} data={data2} heading="MOST  TRANSCATIONS " />
        </div>
        <div className="table-column">
          <Table headers={['Share', 'Close', 'Percent']} data={data3} heading="MOST INCREASED"/>
        </div>
        <div className="table-column">
          <Table headers={['Share', 'Close', 'Percent']} data={data4} heading="MOST REDUCED" />
        </div>
      </div>


    </div>
    </>

  )
}

function ChatBot(){
  const [messages, setMessages] = useState([]);

  const handleSendMessage = (text) => {
    const newMessage = { text, isBot: false };
    setMessages([...messages, newMessage]);

    // Here you can call the API to get a response from the ChatGPT model
    // and add it to the messages state as a bot message
  }

  return (
    <div className="App">
      <h1>My Chatbot App</h1>
      <div className="chat-container">
        {messages.map((message, index) => (
          <ChatMessage key={index} text={message.text} isBot={message.isBot} />
        ))}
      </div>
      <ChatInput onSendMessage={handleSendMessage} />
    </div>
  );
}
export default App;
